mapper.insert:
INSERT INTO SYS_AUTH (
	AUTH_SEQ
	, AUTH_CD
	, AUTH_NM
	, REG_USER_SEQ
	, REG_DT
)
SELECT 
	NEXT VALUE FOR SYS_AUTH_SEQ
	, #{auth_cd}
	, #{auth_nm}
	, #{login_user_seq}
	, GETDATE()

mapper.update:
UPDATE SYS_AUTH 
	SET
		AUTH_CD = #{auth_cd}
		,AUTH_NM = #{auth_nm}
		,MODI_USER_SEQ = #{login_user_seq}
		,MODI_DT = GETDATE()
WHERE AUTH_SEQ = #{auth_seq}
