mapper.insert:
INSERT INTO GEM_ITEM (
	ITEM_SEQ
	, ITEM_CD
	, ITEM_TITLE
	, ITEM_CONTENT
	, ITEM_TYPE
	, ITEM_MENU
	, ITEM_STATUS_CD
	
	, ITEM_LIML_DT
	
	, STEP
	, REG_USER_SEQ
	, REG_DT
)
SELECT 
	NEXT VALUE FOR GEM_ITEM_SEQ
	, #{item_cd}
	, #{item_title}
	, #{item_content}
	, #{item_type}
	, #{item_menu}
	, #{item_status_cd}
	
	, #{item_liml_dt}
	
	, #{login_user_seq}
	, GETDATE()

mapper.update:
UPDATE SYS_AUTH 
	SET
		AUTH_CD = #{auth_cd}
		,AUTH_NM = #{auth_nm}
		,MODI_USER_SEQ = #{login_user_seq}
		,MODI_DT = GETDATE()
WHERE AUTH_SEQ = #{auth_seq}
