mapper.insert:
INSERT INTO SYS_BOARD (
	BOARD_SEQ
	, TITLE
	, REG_USER_SEQ
	, REG_DT
	, SORT
	, CONTENT_TXT
	, CONTENT_HTML
)
SELECT 
	NEXT VALUE FOR SYS_BOARD_SEQ
	, #{notice_title}
	, #{login_user_seq}
	, GETDATE()
	, #{sort}
	, #{content_txt}
	, #{content_html}

mapper.update:
UPDATE SYS_BOARD 
	SET
		TITLE = #{notice_title}
		,SORT = #{sort} 
		,MODI_USER_SEQ = #{login_user_seq}
		,MODI_DT = GETDATE()
		,CONTENT_TXT = #{content_txt}
		,CONTENT_HTML = #{content_html}
WHERE BOARD_SEQ = #{board_seq}
