mapper.insert:
      INSERT INTO SYS_CODE_GROUP (
      	 CD_GRP_SEQ, CD_GRP_CD, CD_GRP_NM 
      	 
			, [DESC]
	      
      	 ,REG_USER_SEQ , REG_DT
      	 ) 
      SELECT NEXT VALUE FOR SYS_CODE_GROUP_SEQ, #{cd_grp_cd},#{cd_grp_nm}
      
			, #{desc}
	  
		, 2, GETDATE()

mapper.update:
UPDATE SYS_CODE_GROUP
SET
   CD_GRP_CD = #{cd_grp_cd}
   ,CD_GRP_NM = #{cd_grp_nm}
   ,[DESC] = #{desc}
   ,MODI_USER_SEQ = #{req_user_seq}
   ,MODI_DT = GETDATE()
   ,DB_TYPE = NULL
WHERE 1=1
   AND CD_GRP_SEQ = #{cd_grp_seq}
