mapper.insert:
INSERT INTO SYS_BOARD (
	BOARD_SEQ
	, TITLE
	, REG_USER_SEQ
	, REG_DT
	, SORT
)
SELECT 
	NEXT VALUE FOR SYS_BOARD_SEQ
	, #{title}
	, #{login_user_seq}
	, GETDATE()
	, #{sort}

mapper.update:
UPDATE SYS_BOARD 
	SET
		AUTH_CD = #{auth_cd}
		,AUTH_NM = #{auth_nm}
		,MODI_USER_SEQ = #{login_user_seq}
		,MODI_DT = GETDATE()
WHERE AUTH_SEQ = #{auth_seq}
